INSERT INTO `tbl_lesson` (`lesson_id`, `lesson_active`, `lesson_weeks`, `lesson_subject`, `lesson_name`, `lesson_info`, `lesson_doc`) VALUES
(1, 1, '1', 28, 'Course Introduction, Introduction to C/C++ (Giới thiệu C/C++)', 'Trong bài học này các bạn sẽ làm quen với những khái niệm cơ bản của C/C++, môi trường lập trình và các quy tắc căn bản khi làm việc với ngôn ngữ', NULL),
(2, 1, '2', 28, 'Data types and variables (Kiểu dữ liệu và biến)', 'Bài giảng nói về các kiểu dữ liệu cũng như biến trong C/C++', NULL),
(3, 1, '3', 28, 'Control Flow (Các cấu trúc điều khiển)', 'Các cấu trúc điều khiển là một phần quan trọng đối với mỗi ngôn ngữ lập trình. Bạn sẽ được làm quen với những khái niệm đơn giản nhất về nó trong C/C++', NULL),
(4, 1, '4', 28, 'Functions (Hàm)', 'Hàm - Cách tổ chức code dưới dạng hàm luôn là điều quan trọng khi lập trình', NULL),
(5, 1, '5', 28, 'Arrays (Mảng)', 'Một loại cấu trúc dữ liệu căn bản và phổ biến với mọi ngôn ngữ lập trình', NULL),
(6, 1, '6', 28, 'Pointers and Strings (Con trỏ và chuỗi)', 'Các bạn sẽ hiểu thế nào là con trỏ trong C/C++, và làm cách nào xử lý một chuỗi trong ngôn ngữ này', NULL),
(7, 1, '7', 28, 'Structures (cấu trúc)', 'Làm quen với các kiểu dữ liệu có cấu trúc', NULL);
